package com.test;

public class AirthameticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10;
int b=2;
int c=10;
System.out.println(a+b);
System.out.println(a-b);
System.out.println(a*b);
System.out.println(a/b);
System.out.println(a%b);

System.out.println(a>b); //10>2
System.out.println(b>a); //2>10
System.out.println(b<a); //2<10
System.out.println(a==b); //10==2
System.out.println(a<=b); //10<=2
System.out.println(a>=b);  //10>=2
System.out.println(c>=a); //10>=10
	}

}
