package com.test;

public class IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10;
int b;
int d=20;
int e;
e=++d; //d=21 and e=21
b=a++; 
System.out.println(a); //11
System.out.println(b);//10
System.out.println(e);
System.out.println(d);
	}

}
